						isShown: function( data ) { return !!( !data.selected.length && data.clicked.name != ".." ); }
					},
					copymove: {
						name: function( data ) {
							if( data.selected.length > 0 )
								return self.i18n.copy+'/'+self.i18n.move+' <span class="badge">'+data.selected.length+'</span>';
