			} else if( e.target.id == "buttonClose" ) {
				e.preventDefault();
				self.isModalClosedByButton = true;
				self.hideModal();
			}
		});

		$('#btnEditoroptions').popover({
			html: true,
			title: self.i18n.options,
			content: function() {
